//랜덤의 한글 생성 함수
function random_hangul() {
	return String.fromCharCode(44031 + Math.ceil(11172 * Math.random()));
}

//난수 생성 함수
function random_number() {
  	return Math.random().toString().substr(2, length);
}

//랜덤 문자열(알파벳 소문자) 생성 함수
function alphabet_small(len) {
	var text = "";
	var charset = "abcdefghijklmnopqrstuvwxyz";
	for (var i = 0; i < len; i++)
		text += charset.charAt(Math.floor(Math.random() * charset.length));
	return text;
}

//랜덤 문자열(알파벳 대문자) 생성 함수
function alphabet_big(len) {
	var text = "";
	var charset = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
	for (var i = 0; i < len; i++)
		text += charset.charAt(Math.floor(Math.random() * charset.length));
	return text;
}

//랜덤 문자열(알파벳 대소문자) 생성 함수
