let pTagCount = 1;
let create_nickname_count = 0;

function create_pTag(length) {
  	let tagArea = document.getElementById("random_nickname_list");
  	let new_pTag = document.createElement("p");

  	new_pTag.setAttribute("class", "pTag");
  	var s = "";
  	if(localStorage.getItem("create_lang") == "hangul")
	{
		for (var j = 0; j < length; j++) s += random_hangul();
	}
		else if(localStorage.getItem("create_lang") == "number")
	{
		for (var j = 0; j < length; j++) s += random_number();
	}
		else if(localStorage.getItem("create_lang") == "alphabet_small")
	{
		s += alphabet_small(length);
	}
		else if(localStorage.getItem("create_lang") == "alphabet_big")
	{
		s += alphabet_big(length);
	}
		else if(localStorage.getItem("create_lang") == "alphabet_big_small")
	{
		s += alphabet_big_small(length);
	}
		else if(localStorage.getItem("create_lang") == "alphabet_number")
	{
		for (var j = 0; j < length; j++) s += random_alphabt_number();
	}
	else if(localStorage.getItem("create_lang") == "alphabet_big_small_number")
	{
		s += alphabet_big_small_number(length);
	}
		else if(localStorage.getItem("create_lang") == "hiragana")
	{
		for (var j = 0; j < length; j++) s += random_hiragana();
	}
	else if(localStorage.getItem("create_lang") == "katakana")
	{
		for (var j = 0; j < length; j++) s += random_katakana();
	}
		else if(localStorage.getItem("create_lang") == "hiragana_katakana")
	{
		for (var j = 0; j < length; j++) s += random_hiragana_katakana();
	}
		else if(localStorage.getItem("create_lang") == "hanja")
	{
		for (var j = 0; j < length; j++) s += random_hanja();
	}
		else if(localStorage.getItem("create_lang") == "emoji")
	{
		s += random_emoji(length);
	}

  	new_pTag.innerHTML = s;

  	tagArea.appendChild(new_pTag);

  	pTagCount++;

  	create_nickname_count++;
}

//타이머
let timer;

function create_pTag_1sec() {
  timer = setInterval(create_pTag, 1000, localStorage.getItem("create_nickname_count"));
  btn_endisable.disabled = true;
  btn_stop_endisable.disabled = false;
  btn_endisable.innerText = auto_creating;
}

function stop_create_PTag_1sec() {
  clearInterval(timer);
  btn_endisable.disabled = false;
  btn_stop_endisable.disabled = true;
  btn_endisable.innerText = btn_menu_create_nickname_per_second;
}
